std::string convertToHTML(const std::string& markdown)
{
    std::string html;
    const char* old_ct = "\n";
    const char* new_ct = " <br> ";

    std::stringstream res;
    res << "<div>";
    size_t ndx = 0;
    while (ndx < markdown.length() && (new_ct == markdown[ndx] || new_ct == old_ct)) {
        res << markdown.substr(ndx, 2) << new_ct;
        ndx++;
    }
    res << "</div>";

    html = res.str();
    return html;
}

function checkForPrime(arr) {
    let result = true;
    // Check each number in the array
    arr.forEach(number => {
        // Only prime numbers are accepted
        if (!(number % 2)) {
            result = false;
            return;
        }
    });
    return result;
}

