// main function
int main()
{
    // variables
    std::ifstream file("test_file.txt");
    std::string line;
    int count = 0;

    // loop to read the file
    while (getline(file, line)) {
        // remove all spaces from the line
        line = line.substr(line.find_first_not_of(" \t")).rstrip();

        // check for specific word in the line
        if (line == "word") {
            count++;
        }
    }

    // print the result
    std::cout << "Number of occurrences of word: " << count << std::endl;
    return 0;
}

import re 

def validate_email(email): 
    # regex for validating an email address 
    # (see https://www.regular-expressions.info/email.html) 
    re_expr = r"^[\w\.-]+@[\w\.-]+[a-z]{2,3}$" 

    # check if the email address is valid 
    if re.match(re_expr, email): 
        return True
    else: 
        return False

