  // Set initial values for min and max
  let min = 0; 
  let max = arr.length - 1;

  // Set initial midpoint
  let mid = Math.floor((min + max) / 2);

  // While min is less than or equal to max
  while(min <= max) {
    // If the element at mid is equal to the target
    if(arr[mid] === target) {
      // Return mid
      return mid;
    }
    // Else if the element at mid is less than the target
    else if(arr[mid] < target) {
      // Set min to mid + 1
      min = mid + 1;
    }
    // Else if the element at mid is greater than the target
    else if(arr[mid] > target) {
      // Set max to mid - 1
      max = mid - 1;
    }

    // Recalculate mid
    mid = Math.floor((min + max) / 2);
  }

  // If the target is not found, return -1
  return -1;
}

